import React, { useState } from "react";

function HistoryPage() {
  // Sample history data (replace with backend data later)
  const [history] = useState([
    {
      id: 1,
      date: "2025-08-28",
      skullName: "Case #101",
      iterations: [
        { id: 3, note: "Final refined output", image: "https://via.placeholder.com/150" },
        { id: 2, note: "Improved skin tone", image: "https://via.placeholder.com/150" },
        { id: 1, note: "Initial reconstruction", image: "https://via.placeholder.com/150" },
      ],
    },
    {
      id: 2,
      date: "2025-08-25",
      skullName: "Case #099",
      iterations: [
        { id: 2, note: "Added hair adjustments", image: "https://via.placeholder.com/150" },
        { id: 1, note: "Base reconstruction", image: "https://via.placeholder.com/150" },
      ],
    },
  ]);

  // Track which attempt is expanded
  const [expanded, setExpanded] = useState(null);

  const toggleExpand = (id) => {
    setExpanded(expanded === id ? null : id);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-200 px-6 py-10">
      <h1 className="text-4xl font-bold text-gray-900 mb-8 text-center">
        Reconstruction History
      </h1>

      <div className="max-w-4xl mx-auto space-y-6">
        {history.map((attempt) => (
          <div
            key={attempt.id}
            className="bg-white shadow-lg rounded-xl overflow-hidden border"
          >
            {/* Header */}
            <div
              className="flex justify-between items-center p-5 cursor-pointer hover:bg-gray-50 transition"
              onClick={() => toggleExpand(attempt.id)}
            >
              <div>
                <h2 className="text-xl font-semibold text-gray-800">
                  {attempt.skullName}
                </h2>
                <p className="text-sm text-gray-500">{attempt.date}</p>
              </div>
              <span className="text-gray-600">
                {expanded === attempt.id ? "▲" : "▼"}
              </span>
            </div>

            {/* Iterations List */}
            {expanded === attempt.id && (
              <div className="p-6 grid grid-cols-1 sm:grid-cols-2 gap-6 bg-gray-50">
                {attempt.iterations.map((iter) => (
                  <div
                    key={iter.id}
                    className="bg-white rounded-lg shadow-md p-4 flex flex-col items-center hover:shadow-xl transition"
                  >
                    <img
                      src={iter.image}
                      alt={`Iteration ${iter.id}`}
                      className="w-32 h-32 object-cover rounded-lg mb-3"
                    />
                    <p className="text-gray-700 font-medium">
                      Iteration {iter.id}
                    </p>
                    <p className="text-sm text-gray-500 text-center mt-1">
                      {iter.note}
                    </p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default HistoryPage;
