import React, { useState } from "react";

function UploadPage() {
  const [progress, setProgress] = useState(0);
  const [stage, setStage] = useState("Waiting for upload...");
  const [image, setImage] = useState(null);

  // Fake processing simulation
  const startProcessing = () => {
    const stages = [
      "Uploading Skull Image...",
      "Checking Completeness...",
      "Species Identification...",
      "Reconstructing Face...",
      "Generating 3D Model...",
      "Finalizing..."
    ];

    let i = 0;
    const interval = setInterval(() => {
      if (i < stages.length) {
        setStage(stages[i]);
        setProgress(((i + 1) / stages.length) * 100);
        i++;
      } else {
        clearInterval(interval);
        setStage("âœ… Completed Successfully!");
      }
    }, 1500);
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
      setProgress(0);
      setStage("Ready to process...");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center px-6 py-12">
      {/* Title */}
      <h1 className="text-4xl font-bold text-gray-900 mb-6">
        Upload Skull Image
      </h1>
      <p className="text-gray-600 mb-8 text-lg max-w-xl text-center">
        Upload a skull image to begin the reconstruction process. The system
        will analyze and guide you through each step automatically.
      </p>

      {/* Progress Bar */}
      <div className="w-full max-w-2xl bg-gray-200 rounded-full h-6 mb-6 shadow-inner">
        <div
          className="bg-blue-600 h-6 rounded-full transition-all duration-500 ease-in-out"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      <p className="text-gray-700 mb-8">{stage}</p>

      {/* Upload Box */}
      <label className="w-full max-w-lg border-4 border-dashed border-gray-300 rounded-2xl p-10 text-center cursor-pointer bg-white shadow-xl hover:shadow-2xl transition">
        <input
          type="file"
          accept="image/*"
          className="hidden"
          onChange={handleFileChange}
        />
        {image ? (
          <img
            src={image}
            alt="Preview"
            className="mx-auto max-h-60 rounded-lg shadow-lg"
          />
        ) : (
          <div>
            <p className="text-gray-600 text-lg">ðŸ“‚ Drag & Drop or Click to Upload</p>
            <p className="text-sm text-gray-400 mt-2">
              Supported formats: JPG, PNG
            </p>
          </div>
        )}
      </label>

      {/* Start Button */}
      {image && (
        <button
          onClick={startProcessing}
          className="mt-8 px-8 py-3 bg-blue-600 text-white font-semibold rounded-full shadow-lg hover:bg-blue-700 transition"
        >
          ðŸš€ Start Processing
        </button>
      )}
    </div>
  );
}

export default UploadPage;
